import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { ALLUSERS, UPDATEUSER } from '../Redux/ActionType';

const Users = () => {
  const [firstname, setFirstname] = useState('');
  const [lastname, setLastname] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [search, setSearch] = useState('');
  const [error, setError] = useState(null);

  const [selectedUser, setSelectedUser] = useState(null);
  const [deleteUser, setDeleteUser] = useState(null);
  const allUsers = useSelector((state) => state.allUsers);
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleAddSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setIsModalOpen(true);

    try {
      const response = await fetch('http://127.0.0.1:8000/register/', {
        method: 'POST',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          first_name: firstname,
          last_name: lastname,
          email,
          phone,
          password,
        }),
      });
      const data = await response.json();

      if (response.ok) {
        setIsModalOpen(false);
        const response = await fetch('http://127.0.0.1:8000/cadmin/users/', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
        });

        const data = await response.json();

        dispatch({
          type: ALLUSERS,
          data: data,
        });
      } else {
        setError(data.error);
      }
    } catch (error) {
      setError('Error occurred during fetch request:', error);
    }
  };

  const handleEditUser = (e, user) => {
    setSelectedUser(user);
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    setIsModalOpen(true);
    await fetch(`http://127.0.0.1:8000/cadmin/user-update/${selectedUser.id}/`, {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        first_name: selectedUser.first_name,
        last_name: selectedUser.last_name,
        email: selectedUser.email,
        phone: selectedUser.phone,
      }),
    })
      .then((res) => {
        const payload = {
          id: selectedUser.id,
          first_name: selectedUser.first_name,
          last_name: selectedUser.last_name,
          email: selectedUser.email,
          phone: selectedUser.phone,
        };
        dispatch({
          type: UPDATEUSER,
          data: payload,
        });
        setIsModalOpen(false);
      })
      .catch((error) => {
        console.error('Error occurred during fetch request:', error);
      });
  };

  const handleSearchUser = async (e) => {
    e.preventDefault();
    const response = await fetch(`http://localhost:8000/cadmin/user-search/?name=${search}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    });
    const data = await response.json();
    dispatch({
      type: ALLUSERS,
      data: data,
    });
  };

  const handleDeleteUser = (e, user) => {
    setDeleteUser(user);
  };

  const handledeleteSubmit = async (e) => {
    e.preventDefault();
    setIsModalOpen(true);
    await fetch(`http://127.0.0.1:8000/cadmin/user-delete/${deleteUser.id}/`, {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
      },
    })
      .then(async (res) => {
        setIsModalOpen(false);
        const response = await fetch('http://127.0.0.1:8000/cadmin/users/', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
        });
        const data = await response.json();
        dispatch({
          type: 'ALLUSERS',
          data: data,
        });
      })
      .catch((error) => {
        console.error('Error occurred during fetch request:', error);
      });
  };

  return (
    <>
      <div className="container-fluid pt-4 px-4">
        <div className="bg-secondary text-center rounded my-4 p-4">
          <div className="d-flex align-items-center justify-content-between mb-4">
            <h6 className="mb-0">Users</h6>
            <form onSubmit={(e) => handleSearchUser(e)}>
              <input
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                type="search"
                placeholder="Search User"
                name="search_users"
              />
              <button className="btn-primary searchbtn" type="submit">
                Search
              </button>
            </form>
          </div>
          <div style={{ textAlign: 'end', marginRight: '2%', marginBottom: '1%' }}>
            <button
              type="button"
              className="btn btn-success"
              data-bs-toggle="modal"
              data-bs-target="#addcouponmodal"
            >
              Add User
            </button>
          </div>
          <div className="table-responsive">
            <table className="table text-start align-middle table-bordered table-hover mb-0">
              <thead>
                <tr className="text-dark">
                  <th scope="col">ID</th>
                  <th scope="col">First Name</th>
                  <th scope="col">Last Name</th>
                  <th scope="col">Email</th>
                  <th scope="col">Phone</th>
                  <th scope="col">Edit</th>
                  <th scope="col">Delete</th>
                </tr>
              </thead>
              <tbody>
                {allUsers &&
                  allUsers.map((user) => (
                    <tr key={user.id}>
                      <td>{user.id}</td>
                      <td>{user.first_name}</td>
                      <td>{user.last_name}</td>
                      <td>{user.email}</td>
                      <td>{user.phone}</td>
                      <td>
                        <button
                          type="button"
                          className="btn btn-primary"
                          data-bs-toggle="modal"
                          data-bs-target="#editCouponModal"
                          onClick={(e) => handleEditUser(e, user)}
                        >
                          Edit
                        </button>
                      </td>
                      <td>
                        <button
                          type="button"
                          className="btn btn-danger"
                          data-bs-toggle="modal"
                          data-bs-target="#deleteProductModal"
                          onClick={(e) => handleDeleteUser(e, user)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      <div
        className={`modal fade ${isModalOpen ? 'show' : ''}`}
        id="addcouponmodal"
        tabIndex="-1"
        aria-labelledby="addcouponmodalLabel"
        aria-hidden="true"
      >
        {/* Add User Modal Content */}
      </div>
      <div className={`modal fade ${isModalOpen ? 'show' : ''}`} id="editCouponModal" tabIndex="-1" role="dialog" aria-labelledby="editCouponModalLabel" aria-hidden="true">
        {/* Edit User Modal Content */}
      </div>
      <div className={`modal fade ${isModalOpen ? 'show' : ''}`} id="deleteProductModal" tabIndex="-1" role="dialog" aria-labelledby="deleteProductModal" aria-hidden="true">
        {/* Delete User Modal Content */}
      </div>
    </>
  );
};

export default React.memo(Users);
